import "core-js/modules/es.array.push.js";
import { eventTypes, dayIndexToDayString, authTypes } from "@/constants";
import { post, put, timeNumToTimeString, dateToTimeNum, getISODateString, isPhone, signInGoogle, getDateWithTimezone, getTimeOptions, addEventToCreatedList } from "@/utils";
import { mapActions, mapState } from "vuex";
import TimezoneSelector from "./schedule_overlap/TimezoneSelector.vue";
import HelpDialog from "./HelpDialog.vue";
import EmailInput from "./event/EmailInput.vue";
import DatePicker from "@/components/DatePicker.vue";
import SlideToggle from "./SlideToggle.vue";
import AlertText from "@/components/AlertText.vue";
import OverflowGradient from "@/components/OverflowGradient.vue";
import { guestUserId } from "@/constants";
import moment from "moment";
import dayjs from "dayjs";
import utcPlugin from "dayjs/plugin/utc";
import timezonePlugin from "dayjs/plugin/timezone";
import ExpandableSection from "./ExpandableSection.vue";
dayjs.extend(utcPlugin);
dayjs.extend(timezonePlugin);
export default {
  name: "NewEvent",
  emits: ["input"],
  props: {
    event: {
      type: Object
    },
    edit: {
      type: Boolean,
      default: false
    },
    dialog: {
      type: Boolean,
      default: true
    },
    contactsPayload: {
      type: Object,
      default: () => ({})
    },
    showHelp: {
      type: Boolean,
      default: false
    },
    folderId: {
      type: String,
      default: null
    }
  },
  components: {
    TimezoneSelector,
    HelpDialog,
    EmailInput,
    DatePicker,
    SlideToggle,
    ExpandableSection,
    AlertText,
    OverflowGradient
  },
  data: () => ({
    formValid: true,
    name: "",
    startTime: 9,
    endTime: 17,
    specificTimesEnabled: false,
    loading: false,
    selectedDays: [],
    // Deleted selectedDaysOfWeek, startOnMonday (feature removed, only 'Dates and times' is supported)
    notificationsEnabled: true,
    // Deleted daysOnly, daysOnlyOptions, dateOptions, selectedDateOption (feature removed, only 'Dates and times' is supported)

    // Email reminders
    showEmailReminders: false,
    emails: [],
    // For email reminders

    // Advanced options
    showAdvancedOptions: false,
    timeIncrement: 15,
    collectEmails: false,
    blindAvailabilityEnabled: false,
    timezone: {},
    sendEmailAfterXResponsesEnabled: false,
    sendEmailAfterXResponses: 3,
    helpDialog: false,
    // Unsaved changes
    initialEventData: {},
    hasMounted: false
  }),
  mounted() {
    if (Object.keys(this.contactsPayload).length > 0) {
      this.toggleEmailReminders(true);

      /** Get previously filled out data after enabling contacts  */
      this.name = this.contactsPayload.name;
      this.startTime = this.contactsPayload.startTime;
      this.endTime = this.contactsPayload.endTime;
      // Deleted daysOnly, selectedDateOption, selectedDaysOfWeek (feature removed)
      this.selectedDays = this.contactsPayload.selectedDays;
      this.notificationsEnabled = this.contactsPayload.notificationsEnabled;
      this.timezone = this.contactsPayload.timezone;
      this.specificTimesEnabled = this.contactsPayload.specificTimesEnabled;
      this.$refs.form.resetValidation();
    }
    this.$nextTick(() => {
      this.hasMounted = true;
    });
  },
  computed: {
    ...mapState(["authUser"]),
    // Deleted daysOnlyEnabled (feature removed)
    nameRules() {
      return [v => !!v || "事件名称是必需填写的噢"];
    },
    selectedDaysRules() {
      return [selectedDays => selectedDays.length > 0 || "请至少选择一个日期"];
    },
    addedEmails() {
      if (Object.keys(this.contactsPayload).length > 0) return this.contactsPayload.emails;
      return this.event && this.event.remindees ? this.event.remindees.map(r => r.email) : [];
    },
    times() {
      return getTimeOptions();
    },
    minCalendarDate() {
      if (this.edit) {
        return "";
      }
      let today = new Date();
      let dd = String(today.getDate()).padStart(2, "0");
      let mm = String(today.getMonth() + 1).padStart(2, "0");
      let yyyy = today.getFullYear();
      return yyyy + "-" + mm + "-" + dd;
    },
    isPhone() {
      return isPhone(this.$vuetify);
    },
    guestEvent() {
      return this.event && this.event.ownerId == guestUserId;
    },
    timeIncrementItems() {
      return [{
        text: "15 min",
        value: 15
      }, {
        text: "30 min",
        value: 30
      }, {
        text: "60 min",
        value: 60
      }];
    }
  },
  methods: {
    ...mapActions(["showError", "setEventFolder"]),
    blurNameField() {
      this.$refs["name-field"].blur();
    },
    reset() {
      this.name = "";
      this.startTime = 9;
      this.endTime = 17;
      this.specificTimesEnabled = false;
      this.selectedDays = [];
      this.selectedDaysOfWeek = [];
      this.notificationsEnabled = true;
      this.daysOnly = false;
      this.selectedDateOption = "Specific dates";
      this.emails = [];
      this.showAdvancedOptions = false;
      this.blindAvailabilityEnabled = false;
      this.sendEmailAfterXResponsesEnabled = false;
      this.sendEmailAfterXResponses = 3;
      this.collectEmails = false;
      this.$refs.form.resetValidation();
    },
    submit() {
      if (!this.$refs.form.validate()) return;
      this.selectedDays.sort();

      // Get duration of event
      let duration = this.endTime - this.startTime;
      if (duration <= 0) duration += 24;

      // Get date objects for each selected day
      let dates = [];
      let type = eventTypes.SPECIFIC_DATES;
      const startTimeString = timeNumToTimeString(this.startTime);
      for (const day of this.selectedDays) {
        const date = dayjs.tz(`${day} ${startTimeString}`, this.timezone.value);
        dates.push(date.toDate());
      }
      this.loading = true;
      const payload = {
        name: this.name,
        duration: duration,
        dates: dates,
        hasSpecificTimes: this.specificTimesEnabled,
        notificationsEnabled: !this.authUser ? false : this.notificationsEnabled,
        blindAvailabilityEnabled: this.blindAvailabilityEnabled,
        remindees: this.emails,
        type: type,
        sendEmailAfterXResponses: this.sendEmailAfterXResponsesEnabled ? parseInt(this.sendEmailAfterXResponses) : -1,
        collectEmails: this.collectEmails,
        timeIncrement: this.timeIncrement,
        creatorPosthogId: this.$posthog?.get_distinct_id()
      };
      const posthogPayload = {
        eventName: this.name,
        eventDuration: duration,
        eventDates: JSON.stringify(dates),
        eventHasSpecificTimes: this.specificTimesEnabled,
        eventNotificationsEnabled: !this.authUser ? false : this.notificationsEnabled,
        eventBlindAvailabilityEnabled: this.blindAvailabilityEnabled,
        eventRemindees: this.emails,
        eventType: type,
        eventSendEmailAfterXResponses: this.sendEmailAfterXResponsesEnabled ? parseInt(this.sendEmailAfterXResponses) : -1,
        eventCollectEmails: this.collectEmails,
        eventTimeIncrement: this.timeIncrement
      };
      if (!this.edit) {
        // Create new event on backend
        post("/events", payload).then(async ({
          eventId,
          shortId
        }) => {
          await this.setEventFolder({
            eventId,
            folderId: this.folderId
          });
          this.$router.push({
            name: "event",
            params: {
              eventId: shortId ?? eventId,
              initialTimezone: this.timezone
            }
          });
          this.$emit("input", false);
          this.reset();
          posthogPayload.eventId = eventId;
          this.$posthog?.capture("Event created", posthogPayload);
          if (!this.authUser) {
            // Add eventId to localStorage, so the user can claim it later
            addEventToCreatedList(eventId);
          }
        }).catch(err => {
          this.showError("There was a problem creating that event! Please try again later.");
          console.error(err);
        }).finally(() => {
          this.loading = false;
        });
      } else {
        // Edit event on backend
        if (this.event) {
          put(`/events/${this.event._id}`, payload).then(() => {
            posthogPayload.eventId = this.event._id;
            this.$posthog?.capture("Event edited", posthogPayload);

            // this.$emit("input", false)
            // this.reset()
            localStorage.setItem(`from-edit-event-${this.event._id}`, "true");
            window.location.reload();
          }).catch(err => {
            this.showError("There was a problem editing this event! Please try again later.");
            console.log(err);
          }).finally(() => {
            this.loading = false;
          });
        }
      }
    },
    toggleEmailReminders(delayed = false) {
      if (delayed) {
        setTimeout(() => this.showEmailReminders = !this.showEmailReminders, 300);
      } else {
        this.showEmailReminders = !this.showEmailReminders;
      }
    },
    /** Redirects user to oauth page requesting access to the user's contacts */
    requestContactsAccess({
      emails
    }) {
      const payload = {
        emails,
        name: this.name,
        startTime: this.startTime,
        endTime: this.endTime,
        selectedDays: this.selectedDays,
        notificationsEnabled: this.notificationsEnabled,
        timezone: this.timezone
      };
      signInGoogle({
        state: {
          type: authTypes.EVENT_CONTACTS,
          eventId: this.event ? this.event.shortId ?? this.event._id : "",
          openNewGroup: false,
          payload
        },
        requestContactsPermission: true
      });
    },
    /** Update state based on the contactsPayload after granting contacts access */
    contactsAccessGranted({
      curScheduledEvent,
      ...data
    }) {
      this.curScheduledEvent = curScheduledEvent;
      this.$refs.confirmDetailsDialog?.setData(data);
      this.confirmDetailsDialog = true;
    },
    /** Populates the form fields based on this.event */
    updateFieldsFromEvent() {
      if (this.event) {
        this.name = this.event.name;

        // Set start time, accounting for the timezone
        this.startTime = Math.floor(dateToTimeNum(getDateWithTimezone(this.event.dates[0]), true));
        this.startTime %= 24;
        this.endTime = (this.startTime + this.event.duration) % 24;
        this.notificationsEnabled = this.event.notificationsEnabled;
        this.blindAvailabilityEnabled = this.event.blindAvailabilityEnabled;
        this.specificTimesEnabled = this.event.hasSpecificTimes;
        if (this.event.dates && this.event.dates.length > 0) {
          // Only support SPECIFIC_DATES
          this.selectedDays = this.event.dates.map(date => getISODateString(getDateWithTimezone(date)));
        }
      }
    },
    resetToEventData() {
      this.updateFieldsFromEvent();
      this.$refs.emailInput.reset();
    },
    setInitialEventData() {
      this.initialEventData = {
        name: this.name,
        startTime: this.startTime,
        endTime: this.endTime,
        specificTimesEnabled: this.specificTimesEnabled,
        selectedDays: this.selectedDays,
        notificationsEnabled: this.notificationsEnabled,
        emails: [...this.emails],
        blindAvailabilityEnabled: this.blindAvailabilityEnabled,
        sendEmailAfterXResponsesEnabled: this.sendEmailAfterXResponsesEnabled,
        sendEmailAfterXResponses: this.sendEmailAfterXResponses,
        timeIncrement: this.timeIncrement
      };
    },
    hasEventBeenEdited() {
      return this.name !== this.initialEventData.name || this.startTime !== this.initialEventData.startTime || this.endTime !== this.initialEventData.endTime || this.specificTimesEnabled !== this.initialEventData.specificTimesEnabled || JSON.stringify(this.selectedDays) !== JSON.stringify(this.initialEventData.selectedDays) || this.notificationsEnabled !== this.initialEventData.notificationsEnabled || JSON.stringify(this.emails) !== JSON.stringify(this.initialEventData.emails) || this.blindAvailabilityEnabled !== this.initialEventData.blindAvailabilityEnabled || this.sendEmailAfterXResponsesEnabled !== this.initialEventData.sendEmailAfterXResponsesEnabled || this.sendEmailAfterXResponses !== this.initialEventData.sendEmailAfterXResponses;
    },
    trackTimezoneChange(newTimezone) {
      this.$posthog?.capture("timezone_selected_in_new_event_dialog", {
        timezone: newTimezone?.value
      });
    }
  },
  watch: {
    event: {
      immediate: true,
      handler() {
        this.updateFieldsFromEvent();
        this.setInitialEventData();
      }
    }
  }
};
