import { isPhone } from "@/utils";
import { eventTypes } from "@/constants";
import ExpandableSection from "@/components/ExpandableSection.vue";
export default {
  name: "EventOptions",
  components: {
    ExpandableSection
  },
  props: {
    event: {
      type: Object,
      required: true
    },
    showBestTimes: {
      type: Boolean,
      required: true
    },
    hideIfNeeded: {
      type: Boolean,
      required: true
    },
    numResponses: {
      type: Number,
      required: true
    },
    showEventOptions: {
      type: Boolean,
      required: true
    },
    showCalendarEvents: {
      type: Boolean,
      default: false
    },
    startCalendarOnMonday: {
      type: Boolean,
      default: false
    }
  },
  computed: {
    isPhone() {
      return isPhone(this.$vuetify);
    },
    isGroup() {
      return this.event.type === eventTypes.GROUP;
    }
  }
};
